    private void button1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button1ActionPerformed
        textArea1.setText("");
        textArea1.append("RESULTS OF STRING COMPARISONS\n");
        textArea1.append("textField1.getText() == textField2.getText()..?\n");        
        
        // compare with ==
        if (textField1.getText() == textField2.getText()) {
            textArea1.append("YES These are the same strings.\n");
        } else {
            textArea1.append("NO These are different strings.\n");
        }

        textArea1.append("\ntextField1.getText().equals(textField2.getText())...?\n");
        
         // compare .equals()
        if (textField1.getText().equals(textField2.getText())) {
            textArea1.append("YES These Strings contain the same letters.\n");
        } else {
            textArea1.append("NO These Strings contain different letters.\n");
        }
    }//GEN-LAST:event_button1ActionPerformed

    private void button2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button2ActionPerformed
        // Assign String1 to CopyOfString1
        String string1; 
        String string2;
        
        string1 = textField1.getText();
        string2 = string1;

        textArea1.setText("");
        textArea1.append("RESULTS when String1 is assigned to String2\n");
        
        textArea1.append("String1 == String2..?\n");
        if (string1 == string2) {
            textArea1.append("YES These are the same strings.\n");
        } else {
            textArea1.append("NO These are different strings.\n");
        }

        textArea1.append("\n(String1.equals(String2))...?\n");

        if (string1.equals(string2)) {
            textArea1.append("YES These Strings contain the same letters.\n");
        } else {
            textArea1.append("NO These Strings contain different letters.\n");
        }

    }//GEN-LAST:event_button2ActionPerformed

