    private void strCompareBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_strCompareBtnActionPerformed
        
        String s1 = "Hello";
        String s2 = "Hello";
        
        String s3 = new String("Hello");
        String s4 = new String("Hello");
        
        textArea1.setText("");
        if (s1 == s2) {
            textArea1.append("YES s1 and s2 are the same strings.\n");
        } else {
            textArea1.append("NO s1 and s2 are different strings.\n");
        }
        
        if (s3 == s4) {
            textArea1.append("YES s3 and s4 are the same strings.\n");
        } else {
            textArea1.append("NO s3 and s4 are different strings.\n");
        }
    }//GEN-LAST:event_strCompareBtnActionPerformed

